#! perl -w 
my $i=0;
my $j=0;

open PICK,">v119silva.SSU_PICK.fasta" || die "$!";
open NUM,">>NUM" || die "$!";
open REF,"v119silva.SSU.fasta" || die "$!";
while(my $line=<REF>)
{

	if ($line =~ />/)
	{
	    chomp ($line);
	    $i++;
		print NUM "$i--$line\t";
	    print PICK "$line\n";  
	}
	else 
	{	
	    chomp ($line);
		$line=~ s/^(\.){1043}/...........................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................NNNNNNNNNNNNNNNNNNNNAGAGTTTGATYMTGGCTCAG/;
	    $line=~ s/(\.){6884}\z/AAGTCGTAACAAGGTANNNNNNNNNNNNNNNNNNNN................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................................/;
		$j++;
		print NUM "$j\n";
		if ($j=~ m/.*1$/){
		print NUM "----------------------------------------$line\n";
		}
		print PICK "$line\n";  
	}
}
close NUM;
close REF;
close PICK;
